import React, { Component } from 'react'
import './content_pane_container.css'
import ListView from '../cardview/cardview_component'
import CardView from '../listview/listview_component'
import * as THREE from "three";

function RenderedView(props){
    if(props.listView == true){
        return <CardView/>;
    }
    else{
        return <ListView/>;
    }
}

class ContentPaneContainer extends Component{

    render() {
        this.renderer.render(this.scene,this.camera);
        this.requestAnimationFrame(render);
      }

    init() {
        this.scene = new THREE.Scene();
        this.camera = new THREE.PerspectiveCamera(60,window.innerWidth / window.innerHeight,1,1000);
        this.camera.position.z = 1;
        this.camera.rotation.x = 1.16;
        this.camera.rotation.y = -0.12;
        this.camera.rotation.z = 0.27;
        var ambient = new THREE.AmbientLight(0x555555);
        this.scene.add(ambient);
        this.renderer = new THREE.WebGLRenderer();
        this.renderer.setSize(window.innerWidth,window.innerHeight);
        this.scene.fog = new THREE.FogExp2(0x03544e, 0.001);
        this.renderer.setClearColor(this.scene.fog.color);
        this.mount.appendChild(this.renderer.domElement);
        this.render;
      }

    componentDidMount(){
        this.init();
    };


    render() {
        return (       
        <div className="content-pane-container"
        ref={mount => { this.mount = mount}}>
            {/* { <RenderedView listView={true}/> } */}
        </div>
        )}
}

export default ContentPaneContainer;
