import { useContext } from 'react'
import './media_container.css'
import ListView from './components/listview/listview_component'
import CardView from './components/cardview/cardview_component'

function ContentPaneContainer(){


    const {
        listView
    } = useContext(media)

    function RenderedView(props){
        if(props.listView){
            return ListView;
        }
        return CardView;
    }

    return (
        <div className="content-pane-container">
            <RenderedView listView={true}/>
        </div>
    )
}

export default ContentPaneContainer;
