import React, { Component } from 'react'
import './content_pane_container.css'
import ListView from '../cardview/cardview_component'
import CardView from '../listview/listview_component'
import * as THREE from "three";

function RenderedView(props){
    if(props.listView == true){
        return <CardView/>;
    }
    else{
        return <ListView/>;
    }
}

class ContentPaneContainer extends Component{

    init = function () {
        let scene = new THREE.Scene();
        let camera = new THREE.PerspectiveCamera(60,window.innerWidth / window.innerHeight,1,1000);
        camera.position.z = 1;
        camera.rotation.x = 1.16;
        camera.rotation.y = -0.12;
        camera.rotation.z = 0.27;
        let ambient = new THREE.AmbientLight(0x555555);
        scene.add(ambient);
        let renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth,window.innerHeight);
        scene.fog = new THREE.FogExp2(0x03544e, 0.001);
        renderer.setClearColor(scene.fog.color);
        this.mount.appendChild(renderer.domElement);
        render();
      }

      render=  function () {
        renderer.render(scene,camera);
        requestAnimationFrame(render);
      }

    componentDidMount() {

        init();
      }


    render() {
        return (       
        <div className="content-pane-container"
        ref={mount => { this.mount = mount}}>
            {/* { <RenderedView listView={true}/> } */}
        </div>
        )}
}

export default ContentPaneContainer;
