import React from 'react';
import './toggleswitchbar_component.css'
import ToggleSwitchButton from '../toggleswitchbutton/toggleswitchbutton_component.js'
import { app_search_context, search_paradigms } from '../../context/app/app_search_context';

const toggleSearchParadigmAlbum = () => {
    console.log("Changing to ALBUM")
    this.setState(state => ({
        searchParadigm: search_paradigms.album        
    }));
}

const toggleSearchParadigmArtist = () => {
    console.log("Changing to ARTIST")
    this.setState(state => ({
        searchParadigm: search_paradigms.artist        
    }));
}

const toggleSearchParadigmGenre = () => {
    console.log("Changing to GENRE")
    this.setState(state => ({
        searchParadigm: search_paradigms.genre        
    }));
}

const toggleSearchParadigmSong = () => {
    console.log("Changing to SONG")
    this.setState(state => ({
        searchParadigm: search_paradigms.song        
    }));
}

function ToggleSwitchBar(){
    return (
        <>
        <nav className="toggleswitchbar"> 
            <div className="toggleswitchbar-container">
            <ul>
                    <li><ToggleSwitchButton modality={"Album"} onclick={toggleSearchParadigmAlbum}/></li>
                    <li><ToggleSwitchButton modality={"Artist"} onclick={toggleSearchParadigmArtist}/></li>
                    <li><ToggleSwitchButton modality={"Genre"} onclick={toggleSearchParadigmGenre}/></li>
                    <li><ToggleSwitchButton modality={"Song"} onclick={toggleSearchParadigmSong}/></li>
                </ul>
            </div>
        </nav>
        </> 
    );
}



export default ToggleSwitchBar;
