const testRequest = () =>{

        const url = build_api_request_url("neverlur_backend_api", "suggestions/genres", "black")
        axios.get(url)
  .then(function (response) {
    // handle success
    console.log(response.data[0]);
    return response.data;
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
  .then(function () {
    // always executed
  });

}


