       {<h2>CURRENT CONTENT STATE: {content} </h2>}
      <AppSearchContext.Provider value={[search_paradigm, setSearchParadigm]}>
        <ToggleSwitchBar/>
      </AppSearchContext.Provider></div>
    </Router>
    <div className="ContentPane"> 
      <ContentPaneContainer/  >
