import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';
import {Button} from '../navbutton/navbutton'
import { NavigationSearchBar } from '../searchbar/searchbar'
import './navbar_component.css'

function Navbar(){
    const [click, setClick] = useState(false);
    const [button, setButton]  = useState(true);
    //deal with smaller windows
    const showButton = () =>{
        if(window.innerWidth <= 1000)
        {
            setButton(false);
        }
        else
        {
            setButton(true);
        }   
    };

    useEffect(() => {
        showButton();
    }, []);

    window.addEventListener('resize', showButton);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);


    return (
        <>
        <nav className="navbar"> 
            <div className="navbar-container">
                <div className="navbar-logo-container">
                <h2 className="navbar-logo-text">BRAGR</h2>
                    <div className="navbar-searbar-container">
                        <NavigationSearchBar></NavigationSearchBar>
                    </div>
                    </div>
                    <div className='menu-icon' onClick={handleClick}> 
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className='nav-item'>
                            <Link to='/services' className='nav-links' onClick={closeMobileMenu}>
                                Services
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/github' className='nav-links' onClick={closeMobileMenu}>
                                Github
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/login' className='nav-links-mobile' onClick={closeMobileMenu}>
                                Login
                            </Link>
                        </li>
                    </ul>
                    {button && <Button buttonStyle='btn--outline'>LOGIN
                        </Button>}
            </div>
        </nav>
        </> 
    );
}



export default Navbar;
