import React, { Component } from 'react'
import './content_pane_container.css'
import ListView from '../cardview/cardview_component'
import CardView from '../listview/listview_component'
import * as THREE from "three";

function RenderedView(props){
    if(props.listView == true){
        return <CardView/>;
    }
    else{
        return <ListView/>;
    }
}

class ContentPaneContainer extends Component{

    componentDidMount() {
        let scene, camera, renderer, cloudParticles = [];
        var init = function (mount) {
          scene = new THREE.Scene();
          camera = new THREE.PerspectiveCamera(75,window.innerWidth / window.innerHeight,1,1000);
          camera.position.z = 1;
          // camera.rotation.x = 1.16;
          // camera.rotation.y = -0.12;
          // camera.rotation.z = 0.27;
          // let ambient = new THREE.AmbientLight(0x555555);
          // scene.add(ambient);
          //setting alpha towards total transparency
          renderer = new THREE.WebGLRenderer({ alpha: true } );
          renderer.setSize(window.innerWidth,window.innerHeight);
          scene.fog = new THREE.FogExp2(0x03544e, 0.001);
          //default value, 
          renderer.setClearColor(0x000000, 0 );
          mount.appendChild(renderer.domElement);

          let loader = new THREE.TextureLoader();
            loader.load("smoke.png", function(texture){
                let cloudGeo = new THREE.PlaneBufferGeometry(500,500);
                let cloudMaterial = new THREE.MeshLambertMaterial({
                map:texture,
                transparent: true
                });

                for(let p=0; p<77; p++) {
                    let cloud = new THREE.Mesh(cloudGeo, cloudMaterial);
                    cloud.position.set(
                      Math.random()*800 -400,
                      500,
                      Math.random()*500-500
                    );
                    cloud.rotation.x = 1.16;
                    cloud.rotation.y = -0.12;
                    cloud.rotation.z = Math.random()*2*Math.PI;
                    cloud.material.opacity = 0.55;
                    cloudParticles.push(cloud);
                    scene.add(cloud);
                  }
            });

          render();
        }

        var render=  function () {
          cloudParticles.forEach(p => {
              p.rotation.z -= 0.001;
          })
          renderer.render(scene,camera);
          requestAnimationFrame(render);
        }
        init(this.mount);
      }


    render() {
        return (       
        <div className="content-pane-container"
        ref={mount => { this.mount = mount}}>
            {/* { <RenderedView listView={true}/> } */}
        </div>
        )}
}

export default ContentPaneContainer;
