import React, {useContext} from 'react';
import './toggleswitchbar_component.css'
import ToggleSwitchButton from '../toggleswitchbutton/toggleswitchbutton_component.js'
import { AppSearchContext } from '../../context/app/app_search_context';


function ToggleSwitchBar(){
    const [paradigm, setSearchParadigm] = useContext(AppSearchContext);
    return (
        <>
        <nav className="toggleswitchbar"> 
            <div className="toggleswitchbar-container">
        <ul>
        <li><button onClick={() => setSearchParadigm("Album")}>
        Album
      </button></li>
      <li><button onClick={() => setSearchParadigm("Artist")}>
        Artist
      </button></li>
      <li><button onClick={() => setSearchParadigm("Genre")}>
        Genre
      </button></li>
      <li><button onClick={() => setSearchParadigm("Song")}>
        Song
      </button></li>
      </ul>
    </div>
        </nav>
        </> 
    );
}



export default ToggleSwitchBar;
