import axios from 'axios';

export const fetchData = () => 
{
    const genrePromise = fetchGenre();
    return  {
        genre: wrapPromise(genrePromise)
    }
}

const wrapPromise = (promise) => {
    let status = 'pending';
    let result;
    let suspender = promise.then(
        res => {
            status = 'success';
            result = res;
        },
         err => {
             status = 'error';
             result = err;
         }
    );

    return {
        read(){
            if(status === 'pending')
            {
                throw 'suspender';
            }
            else if(status === 'error')
            {
                throw result;
            }
            else if(status === 'success')
            {
                throw result;
            }

        }
    }
}

const fetchGenre = () => {
    console.log('Requesting genre...');
    return axios
        .get('http://localhost:8000/api/genres/1/')
        .then(res = res.data)
        .catch(err => console.log(err));
}
