import { Button } from 'bootstrap';
import React from 'react';
import './toggleswitchbutton_component.css'
import {app_search_context, search_paradigms} from '../../context/app/app_search_context.js'

function ToggleSwitchButton(props){
    //each button should have a different onClick function,
    //given that the state they change to is not dependant on the current one
    return(
        <app_search_context.Consumer>
        {({search_paradigm}) => ( <button
          onClick={this.props.onClick}>
          {this.props.modality}
        </button>
      )}</app_search_context.Consumer>
    )   
}

export default ToggleSwitchButton;
