import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';
import {Button} from '../navbutton/navbutton'
import './modalbar_component.css'

function ToggleSwitchBar(){
    const [click, setClick] = useState(false);
    const [button, setButton]  = useState(true);
    //deal with smaller windows
    const showButton = () =>{
        if(window.innerWidth <= 1000)
        {
            setButton(false);
        }
        else
        {
            setButton(true);
        }   
    };

    useEffect(() => {
        showButton();
    }, []);

    window.addEventListener('resize', showButton);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);


    return (
        <>
        <nav className="toggleswitchbar"> 
            <div className="toggleswitchbar-container">
            <div class="switch-field">
		            <input type="radio" id="radio-three" name="switch-two" value="yes" checked/>
		            <label for="radio-three">One</label>
		            <input type="radio" id="radio-four" name="switch-two" value="maybe" />
		            <label for="radio-four">Two</label>
		            <input type="radio" id="radio-five" name="switch-two" value="no" />
		            <label for="radio-five">Three</label>
	        </div>
            </div>
        </nav>
        </> 
    );
}



export default ToggleSwitchBar;
